mapper.select:
SELECT COUNT(ca_idx) FROM cp_camping WHERE 1
AND ca_sido = #{filterSido}
AND ca_manage_status = #{filterStatus}
AND ca_name LIKE CONCAT('%', #{keyword},'%')
AND (ca_manage_status ="확인중" OR ca_modified_date < (select ca_modified_date from cp_camping_api cca where ca_idx = cc.ca_idx))
ORDER BY ca_idx DESC

SELECT 
	ca_idx, ca_name, ca_sido, ca_sigungu, ca_tel, ca_create_date,
	ca_modified_date, ca_manage_status
FROM cp_camping cc
WHERE 1

	AND ca_sido = #{filterSido}


	AND ca_manage_status = #{filterStatus}


	AND ca_name LIKE CONCAT('%', #{keyword},'%')


	AND (ca_manage_status ="확인중" OR 
	ca_modified_date < (select ca_modified_date from cp_camping_api cca where ca_idx = cc.ca_idx)) 

ORDER BY ca_idx DESC
LIMIT #{amount}
OFFSET #{skip}
