mapper.select:
SELECT a.ct_idx, a.ct_wish_start, a.ct_wish_end, a.ct_title, a.ct_age, a.ct_people_cnt, a.ct_end, a.mb_id, b.ca_img, b.ca_sido, b.ca_name, c.mb_nickname FROM cp_crew_together a JOIN cp_camping b ON a.ca_idx = b.ca_idx JOIN cp_member c ON a.mb_id = c.mb_id WHERE a.ct_end = 0
AND a.ct_gender = #{ct_gender}
AND a.ct_camping_type REGEXP #{ct_camping_type}
AND a.ct_age IN
#{item}
AND a.ct_pet = #{ct_pet}
AND a.ct_tool = #{ct_tool}
AND a.ct_wish_start >= #{ct_wish_start} AND a.ct_wish_end <= #{ct_wish_end}
AND b.cp_name = #{cp_name}
LIMIT #{amount} OFFSET #{skip}

SELECT 
	COUNT(a.ct_idx) 
FROM 
	cp_crew_together a 
JOIN 
	cp_camping b 
ON 
	a.ca_idx = b.ca_idx 
JOIN 
	cp_member c 
ON 
	a.mb_id = c.mb_id 
WHERE 
	a.ct_end = 0 

	AND a.ct_gender = #{ct_gender}


		AND a.ct_camping_type REGEXP #{ct_camping_type}


		AND a.ct_age IN

			#{item}



	AND a.ct_pet = #{ct_pet}


	AND a.ct_tool = #{ct_tool}


	AND a.ct_wish_start >= #{ct_wish_start}
	AND a.ct_wish_end <= #{ct_wish_end}


	AND b.cp_name = #{cp_name}

select count(a.ca_idx) from cp_camping a where a.ca_manage_status != '확인중'
and a.ca_sido IN
#{item}
and a.ca_theme IN
#{item}
and a.ca_pet != '불가능' and a.ca_name like concat('%',#{ca_name},'%')

SELECT a.ca_idx, a.ca_name, a.ca_img, a.ca_intro, a.ca_addr_default, a.ca_sido, a.ca_sigungu, a.ca_theme, a.ca_pet, (SELECT count(cl_idx) FROM cp_camping_like WHERE ca_idx = a.ca_idx) AS cntLike, (SELECT count(cr_idx) FROM cp_camping_review WHERE ca_idx = a.ca_idx) AS cntReview FROM cp_camping a WHERE a.ca_manage_status != '확인중'
and a.ca_sido IN
#{item}
and a.ca_theme IN
#{item}
and a.ca_pet != '불가능' and a.ca_name like concat('%',#{ca_name},'%') ORDER BY ca_idx DESC LIMIT #{amount} OFFSET #{skip}

mapper.insert:
INSERT INTO cp_chat_group
SET
	cg_limit = #{cg_limit}, ct_idx = #{ct_idx}

mapper.update:
UPDATE cp_chat_group
SET
	cg_limit = #{param1}
WHERE
	 ct_idx = #{param2}
